import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent {
  isMobile: boolean = false; // Initialize it as false

  // Listen for window resize events and update isMobile accordingly
  @HostListener('window:resize', ['$event'])
  onResize(event: Event): void {
    this.updateIsMobile();
  }

  // Function to update isMobile based on screen width
  private updateIsMobile(): void {
    this.isMobile = window.innerWidth <= 991; // Adjust the breakpoint as needed
  }
}
<div class="navbar">
  <div class="navbar-content">
    <div class="logo">
      <span>ROSEN Marketplace</span>
    </div>
    <nav class="desktop-nav" *ngIf="isDesktop">
      <a routerLink="/heroes">List</a>
      <a routerLink="/heroes">Post new</a>
    </nav>
    <div class="user-info">
      <span>Hello, (USERNAME)</span>
    </div>
    <div class="mobile-menu-icon" *ngIf="isMobile">
      <div class="bar"></div>
      <div class="bar"></div>
      <div class="bar"></div>
    </div>
  </div>
</div>
