import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  isMenuOpen = false;

  toggleMenu() {
    this.isMenuOpen = !this.isMenuOpen;
  }
}
<div class="main-container">
  <div class="top-bar">
    <div class="left-nav">
      <span>ROSEN Marketplace</span>
      <nav>
        <a routerLink="/heroes">List</a>
        <a routerLink="/heroes">Post new</a>
      </nav>
    </div>
    <div class="right-nav">
      <span>Hello, (USERNAME)</span>
    </div>
    <div class="hamburger-menu">
      <div class="bar"></div>
      <div class="bar"></div>
      <div class="bar"></div>
    </div>
  </div>
  <router-outlet></router-outlet>
</div>
